                            <button className='lt equipped-items-item-meta-color-black'><span className='equipped-items-item-meta-color-span'>검정</span></button>
                            <button className='lt equipped-items-item-meta-color-red'><span className='equipped-items-item-meta-color-span'>빨강</span></button>
                            <button className='lt equipped-items-item-meta-color-orange'><span className='equipped-items-item-meta-color-span'>주황</span></button>
                            <button className='lt equipped-items-item-meta-color-yellow'><span className='equipped-items-item-meta-color-span'>노랑</span></button>
                            <button className='lt equipped-items-item-meta-color-grin'><span className='equipped-items-item-meta-color-span'>초록</span></button>
                            <button className='lt equipped-items-item-meta-color-blue'><span className='equipped-items-item-meta-color-span'>파랑</span></button>
                            <button className='lt equipped-items-item-meta-color-purple'><span className='equipped-items-item-meta-color-span'>보라</span></button>
                            <button className='lt equipped-items-item-meta-color-brown'><span className='equipped-items-item-meta-color-span'>갈색</span></button>
                          </div>
                          <div className='equipped-items-item-meta-colors'>
                            <button className='rt equipped-items-item-meta-color-black'><span className='equipped-items-item-meta-color-span'>검정</span></button>
                            <button className='rt equipped-items-item-meta-color-red'><span className='equipped-items-item-meta-color-span'>빨강</span></button>
                            <button className='rt equipped-items-item-meta-color-orange'><span className='equipped-items-item-meta-color-span'>주황</span></button>
                            <button className='rt equipped-items-item-meta-color-yellow'><span className='equipped-items-item-meta-color-span'>노랑</span></button>
                            <button className='rt equipped-items-item-meta-color-grin'><span className='equipped-items-item-meta-color-span'>초록</span></button>
                            <button className='rt equipped-items-item-meta-color-blue'><span className='equipped-items-item-meta-color-span'>파랑</span></button>
                            <button className='rt equipped-items-item-meta-color-purple'><span className='equipped-items-item-meta-color-span'>보라</span></button>
